import React, {Component} from 'react';

class MenuItemObj {
    constructor(value) {
        this.value = value;
    }
}

const menuItems = [new MenuItemObj('Projects'), new MenuItemObj('About'), new MenuItemObj('Contact')];

class Header extends Component {

    state = {
        imgPath: "",
    }

    toggleMenu = () => {
        this.setState( prevState => {
            return {
                hidden: !prevState.hidden
            };
        } )
        console.log(this.state);
    }

    render() {
        return (
            <nav>
                <h1>Simon Vanleeuw</h1>
                    <div id="menu-button" onClick={this.toggleMenu}><img src={this.state.imgPath}/></div>
                    <div id="menu">
                        <ul>
                            {menuItems.map(menuItem => {
                                <li>{menuItem.value}</li>
                            })}
                        </ul>
                        <button className="primary">Get in touch</button>
                    </div>
            </nav>
        )
    }
}

export default Header;
