import React, {Component} from 'react';
import Menu from './img/menu.svg';

class MenuItemObj {
    constructor(value, id) {
        this.value = value;
        this.id = id;
    }
}

const menuItems = [new MenuItemObj('Projects', '1'), new MenuItemObj('About', '2'), new MenuItemObj('Contact', '3')];

class Header extends Component {

    state = {
        hidden: false,
        menuItems: menuItems,
    }

    render() {
        return (
            <nav>
                <h1>Simon Vanleeuw</h1>
                    <div id="menu-button"><img src={Menu}/></div>
                    <div id="menu">
                        <ul>
                            {this.state.menuItems.map(menuItem => {
                                return (
                                    <li key={menuItem.id}>{menuItem.value}</li>
                                    )
                            })}
                        </ul>
                        <button className="primary">Get in touch</button>
                    </div>
            </nav>
        )
    }
}

export default Header;
