import React, {Component} from 'react';
import Menu from './img/menu.svg';
import X from './img/x.svg';

class MenuItemObj {
    constructor(value, id) {
        this.value = value;
        this.id = id;
    }
}

const menuItems = [new MenuItemObj('Projects', '1'), new MenuItemObj('About', '2'), new MenuItemObj('Contact', '3')];

class Header extends Component {

    state = {
        visible: false,
        menuItems: menuItems,
    }

    toggleMenu = () => {
        console.log('onclick triggered');
        this.setState( prevState => ({ 
            visible: !prevState.visible,
        }))
    }

    render() {

        const isHidden = this.state.visible;
        return (
            <nav>
                <div className="menu-container">
                    <h1>Simon Vanleeuw</h1>
                    <div id="menu-button" onClick={this.toggleMenu}><img src={isHidden ? Menu : X}/></div>
                </div>
                    <div id="menu" className={isHidden ? "visible" : "hidden"}>
                        <ul>
                            {this.state.menuItems.map(menuItem => {
                                return (
                                    <li key={menuItem.id}>{menuItem.value}</li>
                                    )
                            })}
                        </ul>
                        <button className="primary">Get in touch</button>
                    </div>
            </nav>
        )
    }
}

export default Header;
